import {createSlice} from '@reduxjs/toolkit'
const DepartmentSlice=createSlice({
    name:'department',
    initialState:{
        status:'idle',
        department:[]
    },
    reducers:{
        addDepartment:(state, action)
    }
})
export default DepartmentSlice
