import {createSlice} from '@reduxjs/toolkit'
const DepartmentSlice=createSlice({
    name:'department',
    initialState:{
        status:'idle',
        Dele:'',
        department:[]
    },
    reducers:{
        addDepartment:(state, action)=>{
            console.log(action.payload)
            state.department.push(action.payload)
        }
    }
})
export default DepartmentSlice
