// export default TodoListSlice
import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
const TodoListSlice1 = createSlice({
  name: "todoList",
  initialState: { status: "idle", todo: [] },
  reducers: {
    addTodo: (state, action) => {
      console.log(state)
      state.todo.push(action.payload);
    },
    changeCompleted: (state, action) => {
      console.log(state.todo)
      return state.todo.map((el) =>
        el.id === action.payload ? { ...el, completed: !el.completed } : el
      );
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(FetchTodo.pending, (state, action) => {
        state.status = "loading";
      })
      .addCase(FetchTodo.fulfilled, (state, action) => {
        state.todo = action.payload;
        state.status = "idle";
      })
      .addCase(AddNewTodo.pending, (state, action) => {
        state.todo = action.payload;
      })
      .addCase(AddNewTodo.fulfilled,(state, action) => {
        state.todo.push(action.payload);
      })
  },
});
