import React,{useState} from 'react'
import Filter from '../Filter'
import {useDispatch,useSelector} from 'react-redux'
import {addemployee} from './EmployeeSlice'
import { v4 as uuidv4 } from 'uuid'
import {} from '' 
const Employee = () => {
  const dispatch=useDispatch()
  const [name,setname]=useState('')
  const [id,setid]=useState('')
  const handleChangeName=(e)=>{
    setname(e.target.value)
  }
  const handleChangeID=(e)=>{
    setid(e.target.value)
  }
  const handleListenAdd=()=>{
    dispatch(addemployee({
      id:uuidv4(),
      name:name,
      idDepartment:id,
    }))
  }
  return (
    <div className='element'>
    <h1>Employee</h1>
    <Filter name={'Employee'}/>
    <div style={{display:'flex',flexDirection:"column",width:'30%',margin:'auto'}}>
      <input type="text" onChange={handleChangeName} placeholder='name employee' style={{padding:'5px',textAlign:'center'}}/>
      <input type="text" onChange={handleChangeID} placeholder='id Department' style={{padding:'5px',textAlign:'center'}}/>
    </div>
      <>
        <button onClick={handleListenAdd}>Thêm</button>
        <button>Xóa</button>
        <button>Sửa</button>
      </>
    </div>
  )
}

export default Employee
