import {createSelector} from 'reselect'
export const TodoList=(state)=>state.todoList;
export const Search=(state)=>state.filters.search;
export const Status=(state)=>state.filters.status;
export const Priority=(state)=>state.filters.status;
export const Remaining=createSelector(
    TodoList,
    Search,
    Status,
    (todo,search,Status)=>{
        return todo.filter((el)=>{
            if(Status==='ALL')
            {
               return el.name.includes(search) 
            }
            return el.name.includes(search) &&
            (Status!=='ALL' &&Status==='Completed'? el.completed:!el.completed)
        })
    }
)
