import { createSlice } from "@reduxjs/toolkit";
const EmployeeSlice = createSlice({
    name: "employee",
    initialState: { 
        status: "idle", 
        employee: [] 
    },
    reducers: {
        addEmployee:(state,action)=>{
            state.employee.push(action.payload)
        }
    },
});
export default EmployeeSlice;
