const rootreducer=(state=init,action)=>{
    console.log({state,action})
    switch(action.type)
    {
        case 'todoList/addTodo':
            return {
                ...state,
                todoList:[
                    ...state.todoList,
                    action.payload
                ]
            }
        case 'filters/searchFilterChange':
            return {
                ...state,
                filters:{
                    ...state.filters,
                    search:action.payload
                }
            }    
        default:
            return state;
    }
}
export default rootreducer
